<?php	include_once("mysqlManager.php");		include_once("converter.php");		include_once("/../services/security/SecurityManager.php");		class PostalCodeConverter extends converter{				function __construct(){			$this->table_name = "postalcodeconverter";		}						function addRow($pcode, $city="", $province=""){			if (!verifyLoginAdmin()) return false;			$mysql = new mysqlManager();			$id = $mysql->createInsertQuery(array("pcode"=>$pcode, "city"=>$city, "province"=>$province), $this->table_name, true, true);			if ($mysql->error != null){				return -1;			}			return $id;		}				function removeRow($pcode){			if (!verifyLoginAdmin()) return false;			$mysql = new mysqlManager();			$mysql->createDeleteQuery(array("pcode"=>$pcode), $this->table_name, true);			if ($mysql->error != null){				return false;			}			return true;		}				function updateRow($pcode, $city, $prov=""){			$unique = array("pcode"=>$pcode);			$para = array("city"=>$city, "province"=>$prov);			$mysql = new mysqlManager();			$mysql->createUpdateQuery($unique, $para, $this->table_name, true);			if ($mysql->error != null){				return false;			}			return true;		}				function getKey(){			return "pcode";		}				function getIDKey(){			return "pcode";		}			}?>